mapper.insert:
insert into BOARD (id, regdate, name, music, gender, age, phoneNum, email, photo);
values
(#{id}, #{regdate} , #{name} , #{music} , #{gender} , #{age} , #{phoneNum} ,#{email} ,#{photo})

mapper.update:
update BOARD
set regdate=#{regdate}, name=#{name}, music=#{music}, gender=#{gender} , age=#{age} , phoneNum=#{phoneNum} , email=#{email} , photo=#{photo}
where id=#{id}
